    modifier onlyOriginalRelayerOrTagger(uint256 _taggingRecordId) {
        require(
            (taggingRecords[_taggingRecordId].relayer == _msgSender() && etsAccessControls.isRelayer(_msgSender())) ||
                taggingRecords[_taggingRecordId].tagger == _msgSender(),
            "Not authorized"
        );
        _;
    }

    // ============ UUPS INTERFACE ============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        IETSAccessControls _etsAccessControls,
        IETSToken _etsToken,
        IETSTarget _etsTarget,
        uint256 _taggingFee,
        uint256 _platformPercentage,
        uint256 _relayerPercentage
    ) public initializer {
        etsAccessControls = _etsAccessControls;
        etsToken = _etsToken;
        etsTarget = _etsTarget;
        setTaggingFee(_taggingFee);
        setPercentages(_platformPercentage, _relayerPercentage);
    }

    // Ensure that only address with admin role can upgrade.
    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal override onlyAdmin {}

    // ============ OWNER INTERFACE ============

    /**
     * @notice Sets ETSAccessControls on the ETSTarget contract so functions can be
     * restricted to ETS platform only. Note Caller of this function must be deployer
     * or pre-set as admin of new contract.
     *
     * @param _accessControls Address of ETSAccessControls contract.
     */
    function setAccessControls(IETSAccessControls _accessControls) public onlyAdmin {
        require(address(_accessControls) != address(0), "Address cannot be zero");
        require(_accessControls.isAdmin(_msgSender()), "Caller not admin in new contract");
        etsAccessControls = _accessControls;
        emit AccessControlsSet(address(etsAccessControls));
    }

    /// @notice Sets the fee required to tag an NFT asset.
    /// @param _fee Value of the fee in WEI.
    function setTaggingFee(uint256 _fee) public onlyAdmin {
        taggingFee = _fee;
        emit TaggingFeeSet(taggingFee);
    }

    /// @notice Admin functionality for updating the percentages.
    /// @param _platformPercentage percentage for platform.
    /// @param _relayerPercentage percentage for relayer.
    function setPercentages(uint256 _platformPercentage, uint256 _relayerPercentage) public onlyAdmin {
        require(_platformPercentage + _relayerPercentage <= 100, "percentages must not be over 100");
        platformPercentage = _platformPercentage;
        relayerPercentage = _relayerPercentage;

        emit PercentagesSet(platformPercentage, relayerPercentage);
    }

    // ============ PUBLIC INTERFACE ============

    /// @inheritdoc IETS
    function createTaggingRecord(
        uint256[] memory _tagIds,
        uint256 _targetId,
        string calldata _recordType,
        address _tagger
    ) public payable nonReentrant onlyRelayer {
        uint256 tagCount = _tagIds.length;
        require(tagCount > 0, "No tags supplied");
        for (uint256 i; i < tagCount; ++i) {
            require(etsToken.tagExistsById(_tagIds[i]), "Invalid tagId");
        }
        require(bytes(_recordType).length >= 3 && bytes(_recordType).length < 31, "Record type too long");
        require(etsTarget.targetExistsById(_targetId), "Invalid targetId");
        _processTaggingFees(_tagIds);
        _createTaggingRecord(_tagIds, _targetId, _recordType, _msgSender(), _tagger);
    }

    /// @inheritdoc IETS
    function getOrCreateTagId(
        string calldata _tag,
        address payable _creator
    ) public payable onlyRelayer returns (uint256 tokenId) {
        return etsToken.getOrCreateTagId(_tag, payable(_msgSender()), _creator);
    }

    /// @inheritdoc IETS
    function createTag(
        string calldata _tag,
        address payable _creator
    ) public payable nonReentrant onlyRelayer returns (uint256 _tokenId) {
        return etsToken.createTag(_tag, payable(_msgSender()), _creator);
    }

    /// @inheritdoc IETS
    function applyTagsWithRawInput(TaggingRecordRawInput calldata _rawInput, address payable _tagger) public payable {
        // Derive tagIds for the tagStrings.
        uint256 tagCount = _rawInput.tagStrings.length;
        require(tagCount > 0, "No tags supplied");

        uint256[] memory tagIds = new uint256[](tagCount);
        for (uint256 i; i < tagCount; ++i) {
            tagIds[i] = getOrCreateTagId(_rawInput.tagStrings[i], _tagger);
        }

        uint256 taggingRecordId = computeTaggingRecordIdFromRawInput(_rawInput, _msgSender(), _tagger);

        if (taggingRecordExists(taggingRecordId)) {
            appendTags(taggingRecordId, tagIds);
        } else {
            // Derive targetId from targetURI. Will revert if targetURI is empty.
            uint256 targetId = etsTarget.getOrCreateTargetId(_rawInput.targetURI);
            // Require new tagging records be inserted by relayer.
            createTaggingRecord(tagIds, targetId, _rawInput.recordType, _tagger);
        }
    }

    /// @inheritdoc IETS
    function applyTagsWithCompositeKey(
        uint256[] memory _tagIds,
        uint256 _targetId,
        string calldata _recordType,
        address payable _tagger
    ) public payable {
        uint256 tagCount = _tagIds.length;
        require(tagCount > 0, "No tags supplied");

        uint256 taggingRecordId = computeTaggingRecordIdFromCompositeKey(_targetId, _recordType, _msgSender(), _tagger);
        if (taggingRecordExists(taggingRecordId)) {
            appendTags(taggingRecordId, _tagIds);
        } else {
            createTaggingRecord(_tagIds, _targetId, _recordType, _tagger);
        }
    }

    /// @inheritdoc IETS
    function replaceTagsWithRawInput(TaggingRecordRawInput calldata _rawInput, address payable _tagger) public payable {
        uint256 tagCount = _rawInput.tagStrings.length;
        require(tagCount > 0, "No tags supplied");

        uint256[] memory tagIds = new uint256[](tagCount);
        for (uint256 i; i < tagCount; ++i) {
            tagIds[i] = getOrCreateTagId(_rawInput.tagStrings[i], _tagger);
        }

        replaceTags(computeTaggingRecordIdFromRawInput(_rawInput, _msgSender(), _tagger), tagIds);
    }

    /// @inheritdoc IETS
    function replaceTagsWithCompositeKey(
        uint256[] calldata _tagIds,
        uint256 _targetId,
        string memory _recordType,
        address payable _tagger
    ) public payable {
        replaceTags(computeTaggingRecordIdFromCompositeKey(_targetId, _recordType, _msgSender(), _tagger), _tagIds);
    }

    /// @inheritdoc IETS
    function removeTagsWithRawInput(TaggingRecordRawInput calldata _rawInput, address _tagger) public {
        uint256 rawTagCount = _rawInput.tagStrings.length;
        uint256[] memory tagIds = new uint256[](rawTagCount);
        for (uint256 i; i < rawTagCount; ++i) {
            tagIds[i] = etsToken.computeTagId(_rawInput.tagStrings[i]);
        }
        removeTags(computeTaggingRecordIdFromRawInput(_rawInput, _msgSender(), _tagger), tagIds);
    }

    /// @inheritdoc IETS
    function removeTagsWithCompositeKey(
        uint256[] calldata _tagIds,
        uint256 _targetId,
        string memory _recordType,
        address payable _tagger
    ) public {
        removeTags(computeTaggingRecordIdFromCompositeKey(_targetId, _recordType, _msgSender(), _tagger), _tagIds);
    }

    /// @inheritdoc IETS
    function appendTags(
        uint256 _taggingRecordId,
        uint256[] memory _tagIds
    ) public payable nonReentrant onlyOriginalRelayerOrTagger(_taggingRecordId) {
        require(_tagIds.length > 0, "No tags supplied");

        // Filter out new tags from the supplied tags.
        _tagIds = UintArrayUtils.difference(_tagIds, taggingRecords[_taggingRecordId].tagIds);

        if (_tagIds.length > 0) {
            _processTaggingFees(_tagIds);
            _appendTags(_taggingRecordId, _tagIds);
        }
    }

    /// @inheritdoc IETS
    function replaceTags(
        uint256 _taggingRecordId,
        uint256[] memory _tagIds
    ) public payable nonReentrant onlyOriginalRelayerOrTagger(_taggingRecordId) {
        require(_tagIds.length > 0, "No tags supplied");

        // Find all the tags NOT SHARED by the tagging record and the replacement set.
        uint256[] memory notShared = UintArrayUtils.difference(taggingRecords[_taggingRecordId].tagIds, _tagIds);

        // Remove these from the tagging record.
        if (notShared.length > 0) {
            _removeTags(_taggingRecordId, notShared);
        }

        // Filter out new tags from the replacement set
        _tagIds = UintArrayUtils.difference(_tagIds, taggingRecords[_taggingRecordId].tagIds);

        if (_tagIds.length > 0) {
            _processTaggingFees(_tagIds);
            _appendTags(_taggingRecordId, _tagIds);
        }
    }

    /// @inheritdoc IETS
    function removeTags(
        uint256 _taggingRecordId,
        uint256[] memory _tagIds
    ) public nonReentrant onlyOriginalRelayerOrTagger(_taggingRecordId) {
        require(_tagIds.length > 0, "No tags supplied");

        // Find tags shared by supplied tags and tagging record tags.
        _tagIds = UintArrayUtils.intersect(_tagIds, taggingRecords[_taggingRecordId].tagIds);

        if (_tagIds.length > 0) {
            // No tagging fee when tags are removed.
            _removeTags(_taggingRecordId, _tagIds);
        }
    }

    /// @inheritdoc IETS
    function drawDown(address payable _account) external nonReentrant {
        uint256 balanceDue = totalDue(_account);
        if (balanceDue > 0 && balanceDue <= address(this).balance) {
            paid[_account] = paid[_account] + balanceDue;

            (bool success, ) = _account.call{ value: balanceDue }("");
            require(success, "Transfer failed.");

            emit FundsWithdrawn(_account, balanceDue);
        }
    }

    // ============ PUBLIC VIEW FUNCTIONS ============

    /// @inheritdoc IETS
    function computeTaggingRecordIdFromRawInput(
        TaggingRecordRawInput memory _rawInput,
        address _relayer,
        address _tagger
    ) public view returns (uint256 taggingRecordId) {
        return
            computeTaggingRecordIdFromCompositeKey(
                etsTarget.computeTargetId(_rawInput.targetURI),
                _rawInput.recordType,
                _relayer,
                _tagger
            );
    }

    /// @inheritdoc IETS
    function computeTaggingRecordIdFromCompositeKey(
        uint256 _targetId,
        string memory _recordType,
        address _relayer,
        address _tagger
    ) public pure returns (uint256 taggingRecordId) {
        taggingRecordId = uint256(keccak256(abi.encodePacked(_targetId, _recordType, _relayer, _tagger)));
    }

    /// @inheritdoc IETS
    function computeTaggingFeeFromRawInput(
        TaggingRecordRawInput calldata _rawInput,
        address _relayer,
        address _tagger,
        TaggingAction _action
    ) public view returns (uint256 fee, uint256 tagCount) {
        uint256 rawTagCount = _rawInput.tagStrings.length;
        uint256[] memory tagIds = new uint256[](rawTagCount);
        for (uint256 i; i < rawTagCount; ++i) {
            tagIds[i] = etsToken.computeTagId(_rawInput.tagStrings[i]);
        }
        return computeTaggingFee(computeTaggingRecordIdFromRawInput(_rawInput, _relayer, _tagger), tagIds, _action);
    }

    /// @inheritdoc IETS
    function computeTaggingFeeFromCompositeKey(
        uint256[] memory _tagIds,
        uint256 _targetId,
        string calldata _recordType,
        address _relayer,
        address _tagger,
        TaggingAction _action
    ) public view returns (uint256 fee, uint256 tagCount) {
        return
            computeTaggingFee(
                computeTaggingRecordIdFromCompositeKey(_targetId, _recordType, _relayer, _tagger),
                _tagIds,
                _action
            );
    }

    /// @inheritdoc IETS
    function computeTaggingFee(
        uint256 _taggingRecordId,
        uint256[] memory _tagIds,
        TaggingAction _action
    ) public view returns (uint256 fee, uint256 tagCount) {
        // Return quickly when no tagging record exists.
        if (!taggingRecordExists(_taggingRecordId)) {
            return (_computeTaggingFee(_tagIds.length), _tagIds.length);
        }

        if (TaggingAction(_action) == TaggingAction.APPEND) {
            // remove tagging record tag ids from input tag ids to return number of new tags applied.
            _tagIds = UintArrayUtils.difference(_tagIds, taggingRecords[_taggingRecordId].tagIds);
        }

        if (TaggingAction(_action) == TaggingAction.REPLACE) {
            // Remove tags from tagging record not in replacement tag set.
            uint256[] memory taggingRecordTags = taggingRecords[_taggingRecordId].tagIds;
            uint256[] memory tagsToRemove = UintArrayUtils.difference(taggingRecords[_taggingRecordId].tagIds, _tagIds);
            if (tagsToRemove.length > 0) {
                taggingRecordTags = UintArrayUtils.difference(taggingRecords[_taggingRecordId].tagIds, tagsToRemove);
            }

            _tagIds = UintArrayUtils.difference(_tagIds, taggingRecordTags);
        }

        if (TaggingAction(_action) == TaggingAction.REMOVE) {
            // Find tags shared by supplied tags and tagging record tags.
            _tagIds = UintArrayUtils.intersect(_tagIds, taggingRecords[_taggingRecordId].tagIds);

            // No fee charged for removing tags at the present time, but still nice to give
            // clients a way to know how many tags will be removed.
            return (0, _tagIds.length);
        }

        return (_computeTaggingFee(_tagIds.length), _tagIds.length);
    }

    /// @inheritdoc IETS
    function getTaggingRecordFromRawInput(
        TaggingRecordRawInput memory _rawInput,
        address _relayer,
        address _tagger
    )
        public
        view
        returns (uint256[] memory tagIds, uint256 targetId, string memory recordType, address relayer, address tagger)
    {
        return
            this.getTaggingRecordFromId(
                computeTaggingRecordIdFromCompositeKey(
                    etsTarget.computeTargetId(_rawInput.targetURI),
                    _rawInput.recordType,
                    _relayer,
                    _tagger
                )
            );
    }

    /// @inheritdoc IETS
    function getTaggingRecordFromCompositeKey(
        uint256 _targetId,
        string memory _recordType,
        address _relayer,
        address _tagger
    )
        public
        view
        returns (uint256[] memory tagIds, uint256 targetId, string memory recordType, address relayer, address tagger)
    {
        return
            this.getTaggingRecordFromId(
                computeTaggingRecordIdFromCompositeKey(_targetId, _recordType, _relayer, _tagger)
            );
    }

    /// @inheritdoc IETS
    function getTaggingRecordFromId(
        uint256 _id
    )
        public
        view
        returns (uint256[] memory tagIds, uint256 targetId, string memory recordType, address relayer, address tagger)
    {
        TaggingRecord storage taggingRecord = taggingRecords[_id];
        return (
            taggingRecord.tagIds,
            taggingRecord.targetId,
            taggingRecord.recordType,
            taggingRecord.relayer,
            taggingRecord.tagger
        );
    }

    /// @inheritdoc IETS
    function taggingRecordExistsByRawInput(
        TaggingRecordRawInput memory _rawInput,
        address _relayer,
        address _tagger
    ) public view returns (bool) {
        return
            taggingRecordExists(
                computeTaggingRecordIdFromCompositeKey(
                    etsTarget.computeTargetId(_rawInput.targetURI),
                    _rawInput.recordType,
                    _relayer,
                    _tagger
                )
            );
    }

    /// @inheritdoc IETS
    function taggingRecordExistsByCompositeKey(
        uint256 _targetId,
        string memory _recordType,
        address _relayer,
        address _tagger
    ) public view returns (bool) {
        return taggingRecordExists(computeTaggingRecordIdFromCompositeKey(_targetId, _recordType, _relayer, _tagger));
    }

    /// @inheritdoc IETS
    function taggingRecordExists(uint256 _taggingRecordId) public view returns (bool) {
        return taggingRecords[_taggingRecordId].targetId != 0 ? true : false;
    }

    /// @inheritdoc IETS
    function totalDue(address _account) public view returns (uint256 _due) {
        return accrued[_account] - paid[_account];
    }

    // ============ INTERNAL FUNCTIONS ============

    /// @dev write a tagging record, mapping a taggingRecordId to a TaggingRecord struct.
    function _createTaggingRecord(
        uint256[] memory _tagIds,
        uint256 _targetId,
        string calldata _recordType,
        address _relayer,
        address _tagger
    ) private {
        uint256 taggingRecordId = computeTaggingRecordIdFromCompositeKey(_targetId, _recordType, _relayer, _tagger);
        taggingRecords[taggingRecordId] = TaggingRecord({
            tagIds: _tagIds,
            targetId: _targetId,
            recordType: _recordType,
            relayer: _relayer,
            tagger: _tagger
        });

        emit TaggingRecordCreated(taggingRecordId);
    }

    /**
     * @dev Append tags to a tagging record
     */
    function _appendTags(uint256 _taggingRecordId, uint256[] memory _tagIds) private {
        // Merge _newTagIds with existing tags.
        taggingRecords[_taggingRecordId].tagIds = UintArrayUtils.extend(
            taggingRecords[_taggingRecordId].tagIds,
            _tagIds
        );
        emit TaggingRecordUpdated(_taggingRecordId, TaggingAction.APPEND);
    }

    /**
     * @dev Remove tags from tagging record.
     *
     * @param _taggingRecordId tagging record being updated.
     * @param _tagIds tagId to remove from tagging record.
     */
    function _removeTags(
        uint256 _taggingRecordId,
        uint256[] memory _tagIds
    ) private onlyOriginalRelayerOrTagger(_taggingRecordId) {
        taggingRecords[_taggingRecordId].tagIds = UintArrayUtils.difference(
            taggingRecords[_taggingRecordId].tagIds,
            _tagIds
        );
        emit TaggingRecordUpdated(_taggingRecordId, TaggingAction.REMOVE);
    }

    function _computeTaggingFee(uint256 _tagCount) internal view returns (uint256 _fee) {
        uint256 fee = 0;
        if (_tagCount > 0 && taggingFee > 0) {
            fee = _tagCount * taggingFee;
        }
        return (fee);
    }

    function _processTaggingFees(uint256[] memory _tagIds) private {
        require((msg.value == _computeTaggingFee(_tagIds.length)), "wrong fee supplied");
        address platform = etsAccessControls.getPlatformAddress();
        for (uint256 i; i < _tagIds.length; ++i) {
            _processAccrued(_tagIds[i], platform);
        }
    }

    // @dev Internal function to divide up the tagging fee and accrue it to ETS participants.
    function _processAccrued(uint256 _tagId, address _platform) private {
        // Note: This will cause _processTaggingFees to revert if tagId doesn't exist.
        address owner = etsToken.ownerOf(_tagId);
        IETSToken.Tag memory tag = etsToken.getTagById(_tagId);

        uint256 platformAllocation = (msg.value * platformPercentage) / MODULO;
        uint256 relayerAllocation = (msg.value * relayerPercentage) / MODULO;
        uint256 remainingAllocation = msg.value - (platformAllocation + relayerAllocation);

        accrued[_platform] = accrued[_platform] + platformAllocation;
        accrued[tag.relayer] = accrued[tag.relayer] + relayerAllocation;

        // pre-auction.
        if (owner == _platform) {
            accrued[tag.creator] = accrued[tag.creator] + remainingAllocation;
        }
        // post-auction.
        else {
            accrued[owner] = accrued[owner] + remainingAllocation;
        }
    }
}

contracts/ETSAccessControls.sol.content:
// SPDX-License-Identifier: MIT

/**
 * @title IETSAccessControls
 * @author Ethereum Tag Service <team@ets.xyz>
 *
 *  
 *  
 *          
 *          
 *        
 *        
 *
 * @notice This is the interface for the ETSAccessControls contract which allows ETS Core Dev
 * Team to administer roles and control access to various parts of the ETS Platform.
 * ETSAccessControls contract contains a mix of public and administrator only functions.
 */

pragma solidity ^0.8.10;

import { IETSAccessControls } from "./interfaces/IETSAccessControls.sol";
import { IETSRelayer } from "./relayers/interfaces/IETSRelayer.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { AccessControlUpgradeable } from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import { ERC165CheckerUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165CheckerUpgradeable.sol";

contract ETSAccessControls is Initializable, AccessControlUpgradeable, IETSAccessControls, UUPSUpgradeable {
    /// Public constants
    string public constant NAME = "ETS access controls";
    bytes32 public constant RELAYER_ROLE = keccak256("RELAYER_ROLE");
    bytes32 public constant RELAYER_FACTORY_ROLE = keccak256("RELAYER_FACTORY_ROLE");
    bytes32 public constant RELAYER_ADMIN_ROLE = keccak256("RELAYER_ADMIN_ROLE");
    bytes32 public constant AUCTION_ORACLE_ROLE = keccak256("AUCTION_ORACLE_ROLE");
    bytes32 public constant SMART_CONTRACT_ROLE = keccak256("SMART_CONTRACT_ROLE");

    /// @dev ETS Platform account. Core Dev Team multisig in production.
    /// There will only be one "Platform" so no need to make it a role.
    address payable internal platform;

    /// @notice Mapping to contain whether Relayer is paused by the protocol.
    mapping(address => bool) public relayerLocked;

    /// @notice Relayer name to contract address.
    mapping(string => address) public relayerNameToContract;

    /// @notice Relayer contract address to human readable name.
    mapping(address => string) public relayerContractToName;

    /// @notice Relayer owner address to relayer address.
    mapping(address => address) public relayerOwnerToAddress;

    modifier onlyValidName(string calldata _name) {
        require(!isRelayerByName(_name), "Relayer name exists");
        bytes memory nameBytes = bytes(_name);
        require(nameBytes.length >= 2, "Relayer name too short");
        require(nameBytes.length <= 32, "Relayer name too long");
        _;
    }

    // ============ UUPS INTERFACE ============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _platformAddress) public initializer {
        __AccessControl_init();
        _grantRole(DEFAULT_ADMIN_ROLE, _msgSender());
        setPlatform(payable(_platformAddress));
    }

    // Ensure that only addresses with admin role can upgrade.
    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal override onlyRole(DEFAULT_ADMIN_ROLE) {}

    // ============ OWNER INTERFACE ============

    /// @inheritdoc IETSAccessControls
    function setPlatform(address payable _platform) public onlyRole(DEFAULT_ADMIN_ROLE) {
        address prevAddress = platform;
        platform = _platform;
        grantRole(DEFAULT_ADMIN_ROLE, _platform);
        emit PlatformSet(_platform, prevAddress);
    }

    /// @inheritdoc IETSAccessControls
    function setRoleAdmin(bytes32 _role, bytes32 _adminRole) public onlyRole(DEFAULT_ADMIN_ROLE) {
        _setRoleAdmin(_role, _adminRole);
    }

    /// @inheritdoc IETSAccessControls
    function registerRelayer(
        address _relayer,
        string calldata _name,
        address _owner
    ) public onlyRole(RELAYER_FACTORY_ROLE) {
        relayerNameToContract[_name] = _relayer;
        relayerContractToName[_relayer] = _name;
        relayerOwnerToAddress[_owner] = _relayer;
        relayerLocked[_relayer] = false;
        // Note: grantRole emits RoleGranted event.
        grantRole(RELAYER_ROLE, _relayer);
        emit RelayerAdded(_relayer);
    }

    /// @inheritdoc IETSAccessControls
    function pauseRelayerByOwnerAddress(address _relayerOwner) public onlyRole(RELAYER_ADMIN_ROLE) {
        if (isRelayerByOwner(_relayerOwner)) {
            IETSRelayer relayer = IETSRelayer(getRelayerAddressFromOwner(_relayerOwner));
            if (!relayer.isPaused()) {
                relayer.pause();
            }
        }
    }

    /// @inheritdoc IETSAccessControls
    function changeRelayerOwner(address _currentOwner, address _newOwner) public onlyRole(RELAYER_ROLE) {
        require(isRelayerByAddress(_msgSender()), "Caller is not relayer");
        require(IETSRelayer(_msgSender()).getOwner() == _currentOwner, "Not relayer owner");
        require(!isRelayerByOwner(_newOwner), "New owner already owns a relayer");
        relayerOwnerToAddress[_currentOwner] = address(0);
        // _msgSender() is the relayer itself.
        relayerOwnerToAddress[_newOwner] = _msgSender();
    }

    /// @inheritdoc IETSAccessControls
    function toggleRelayerLock(address _relayer) public onlyRole(RELAYER_ADMIN_ROLE) {
        relayerLocked[_relayer] = !relayerLocked[_relayer];
        emit RelayerLockToggled(_relayer);
    }

    // ============ PUBLIC VIEW FUNCTIONS ============

    /// @inheritdoc IETSAccessControls
    function isSmartContract(address _addr) public view returns (bool) {
        return hasRole(SMART_CONTRACT_ROLE, _addr);
    }

    /// @inheritdoc IETSAccessControls
    function isAdmin(address _addr) public view returns (bool) {
        return hasRole(DEFAULT_ADMIN_ROLE, _addr);
    }

    /// @inheritdoc IETSAccessControls
    function isAuctionOracle(address _addr) public view returns (bool) {
        return hasRole(AUCTION_ORACLE_ROLE, _addr);
    }

    /// @inheritdoc IETSAccessControls
    function isRelayerFactory(address _addr) public view returns (bool) {
        return hasRole(RELAYER_ADMIN_ROLE, _addr) || hasRole(RELAYER_FACTORY_ROLE, _addr);
    }

    /// @inheritdoc IETSAccessControls
    function isRelayer(address _addr) public view returns (bool) {
        return hasRole(RELAYER_ADMIN_ROLE, _addr) || isRelayerAndNotPaused(_addr);
    }

    /// @inheritdoc IETSAccessControls
    function isRelayerLocked(address _addr) public view returns (bool) {
        return relayerLocked[_addr];
    }

    /// @inheritdoc IETSAccessControls
    function isRelayerAndNotPaused(address _addr) public view returns (bool) {
        return isRelayerByAddress(_addr) && !isRelayerLocked(_addr) && !IETSRelayer(_addr).isPaused();
    }

    /// @inheritdoc IETSAccessControls
    function isRelayerByOwner(address _addr) public view returns (bool) {
        return relayerOwnerToAddress[_addr] != address(0);
    }

    /// @inheritdoc IETSAccessControls
    function isRelayerAdmin(address _addr) public view returns (bool) {
        return hasRole(RELAYER_ADMIN_ROLE, _addr);
    }

    /// @inheritdoc IETSAccessControls
    function isRelayerByName(string memory _name) public view returns (bool) {
        return relayerNameToContract[_name] != address(0);
    }

    /// @inheritdoc IETSAccessControls
    function isRelayerByAddress(address _addr) public view returns (bool) {
        return keccak256(abi.encodePacked(relayerContractToName[_addr])) != keccak256(abi.encodePacked(""));
    }

    /// @inheritdoc IETSAccessControls
    function getRelayerAddressFromName(string memory _name) public view returns (address) {
        return relayerNameToContract[_name];
    }

    /// @inheritdoc IETSAccessControls
    function getRelayerNameFromAddress(address _address) public view returns (string memory) {
        return relayerContractToName[_address];
    }

    /// @inheritdoc IETSAccessControls
    function getRelayerAddressFromOwner(address _address) public view returns (address) {
        return relayerOwnerToAddress[_address];
    }

    /// @inheritdoc IETSAccessControls
    function getPlatformAddress() public view returns (address payable) {
        return platform;
    }
}

contracts/ETSAuctionHouse.sol.content:
// SPDX-License-Identifier: GPL-3.0

/**
 * @title ETS Auction House
 * @author Ethereum Tag Service <team@ets.xyz>
 *
 *  
 *  
 *          
 *          
 *        
 *        
 *
 * @notice ETSAuctionHouse.sol is a modified version of Nouns NounsAuctionHouse.sol:
 * https://github.com/nounsDAO/nouns-monorepo/blob/master/packages/nouns-contracts/contracts/NounsAuctionHouse.sol
 * which itself is a modified version of Zora AuctionHouse.sol
 *
 * AuctionHouse.sol source code Copyright Zora licensed under the GPL-3.0 license.
 * With modifications by Ethereum Tag Service.
 *
 * ETS modification include enabling more than one concurrent auction via a protocol controlled
 * setting and requiring a bid to start the auction timer.
 */

pragma solidity ^0.8.6;

import { IWMATIC } from "./interfaces/IWMATIC.sol";
import { IETSToken } from "./interfaces/IETSToken.sol";
import { IETSAuctionHouse } from "./interfaces/IETSAuctionHouse.sol";
import { IETSAccessControls } from "./interfaces/IETSAccessControls.sol";

import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { PausableUpgradeable } from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import { IERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import { SafeERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import { CountersUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";

/**
 * @title ETSAuctionHouse
 * @author Ethereum Tag Service <team@ets.xyz>
 *
 * @notice ETSAuctionHouse contract governs the sale of Ethereum Tag Service composable tags (CTAGs).
 */
contract ETSAuctionHouse is
    Initializable,
    PausableUpgradeable,
    ReentrancyGuardUpgradeable,
    IETSAuctionHouse,
    UUPSUpgradeable
{
    using CountersUpgradeable for CountersUpgradeable.Counter;

    IETSToken public etsToken;
    IETSAccessControls public etsAccessControls;

    /// Public constants

    string public constant NAME = "ETS Auction House";
    uint256 public constant MODULO = 100;

    /// Public variables
    uint256 public maxAuctions;

    // Open for bidding
    CountersUpgradeable.Counter activeAuctions;

    // Unique auction id
    CountersUpgradeable.Counter auctionId;

    /// @dev Mapping of auction ID to auction.
    mapping(uint256 => IETSAuctionHouse.Auction) public auctions;

    /// @dev Mapping of tokenId to array of auction ids.
    mapping(uint256 => uint256[]) public auctionsByTokenId;

    /// @dev Mapping from auctionId to the corresponding tagId
    //mapping(uint256 => uint256) public auctionIdToTokenId;

    // Mapping from auctionId to the corresponding auction for each token
    //mapping(uint256 => mapping(uint256 => Auction)) public auctionIdToAuction;

    /// @dev The address of the WMATIC contract
    address public wmatic;

    /// @dev The minimum amount of time left in an auction after a new bid is created
    uint256 public timeBuffer;

    /// @dev The minimum price accepted in an auction
    uint256 public reservePrice;

    /// @dev The minimum percentage difference between the last bid amount and the current bid
    uint8 public minBidIncrementPercentage;

    /// @dev The duration of a single auction
    uint256 public duration;

    /// @dev Percentage of auction proceeds allocated to CTAG Creator
    uint256 public creatorPercentage;

    /// @dev Percentage of auction proceeds allocated to CTAG Relayer.
    uint256 public relayerPercentage;

    /// @dev Percentage of auction proceeds allocated to ETS.
    uint256 public platformPercentage;

    /// @dev Map for holding amount accrued to participant address wallets.
    mapping(address => uint256) public accrued;

    /// @dev Map for holding lifetime amount drawn down from accrued by participants.
    mapping(address => uint256) public paid;

    /// Modifiers

    modifier tagExists(uint256 tokenId) {
        require(etsToken.tagExistsById(tokenId), "CTAG does not exist");
        _;
    }

    modifier platformOwned(uint256 tokenId) {
        // Returns "ERC721: owner query for nonexistent token" for non-existent token.
        require(etsToken.ownerOf(tokenId) == etsAccessControls.getPlatformAddress(), "CTAG not owned by ETS");
        _;
    }

    modifier onlyAuctionOracle() {
        require(etsAccessControls.isAuctionOracle(_msgSender()), "Caller not auction oracle");
        _;
    }

    modifier onlyAdmin() {
        require(etsAccessControls.isAdmin(_msgSender()), "Caller must be administrator");
        _;
    }

    modifier onlyPlatform() {
        require(etsAccessControls.getPlatformAddress() == _msgSender(), "Only platform may release CTAG");
        _;
    }

    // ============ UUPS INTERFACE ============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        IETSToken _etsToken,
        IETSAccessControls _etsAccessControls,
        address _wmatic,
        uint256 _maxAuctions,
        uint256 _timeBuffer,
        uint256 _reservePrice,
        uint8 _minBidIncrementPercentage,
        uint256 _duration,
        uint256 _relayerPercentage,
        uint256 _platformPercentage
    ) external initializer {
        __Pausable_init();
        __ReentrancyGuard_init();

        _pause();

        etsToken = _etsToken;
        etsAccessControls = _etsAccessControls;
        wmatic = _wmatic;

        activeAuctions.reset();
        auctionId.reset();

        setMaxAuctions(_maxAuctions);
        setMinBidIncrementPercentage(_minBidIncrementPercentage);
        setDuration(_duration);
        setReservePrice(_reservePrice);
        setTimeBuffer(_timeBuffer);
        setProceedPercentages(_platformPercentage, _relayerPercentage);
    }

    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal override onlyAdmin {}

    // ============ OWNER/ADMIN INTERFACE ============

    function pause() public onlyAdmin {
        _pause();
    }

    function unpause() public onlyAdmin {
        _unpause();
    }

    function setMaxAuctions(uint256 _maxAuctions) public onlyAdmin {
        maxAuctions = _maxAuctions;
        emit AuctionsMaxSet(_maxAuctions);
    }

    function setDuration(uint256 _duration) public onlyAdmin {
        duration = _duration;
        emit AuctionDurationSet(_duration);
    }

    function setMinBidIncrementPercentage(uint8 _minBidIncrementPercentage) public onlyAdmin {
        minBidIncrementPercentage = _minBidIncrementPercentage;
        emit AuctionMinBidIncrementPercentageSet(_minBidIncrementPercentage);
    }

    function setReservePrice(uint256 _reservePrice) public onlyAdmin {
        reservePrice = _reservePrice;
        emit AuctionReservePriceSet(_reservePrice);
    }

    function setTimeBuffer(uint256 _timeBuffer) public onlyAdmin {
        timeBuffer = _timeBuffer;
        emit AuctionTimeBufferSet(_timeBuffer);
    }

    function setProceedPercentages(uint256 _platformPercentage, uint256 _relayerPercentage) public onlyAdmin {
        require(_platformPercentage + _relayerPercentage <= 100, "Input must not exceed 100%");
        platformPercentage = _platformPercentage;
        relayerPercentage = _relayerPercentage;
        creatorPercentage = MODULO - platformPercentage - relayerPercentage;

        emit AuctionProceedPercentagesSet(platformPercentage, relayerPercentage, creatorPercentage);
    }

    // ============ PUBLIC INTERFACE ============

    /**
     * @notice Settle auction, and release next auction.
     */
    function settleCurrentAndCreateNewAuction(uint256 _auctionId) public nonReentrant whenNotPaused {
        _settleAuction(_auctionId);
        _requestCreateAuction();
    }

    /**
     * @notice Settle the auction.
     * @dev This function can only be called when the contract is paused.
     */
    function settleAuction(uint256 _auctionId) public whenPaused nonReentrant {
        _settleAuction(_auctionId);
    }

    // Public function to release another auction using the auction Oracle.
    function createNextAuction() public whenNotPaused {
        require(activeAuctions.current() < maxAuctions, "No open auction slots");
        _requestCreateAuction();
    }

    // Capture reponse from next auction oracle.
    function fulfillRequestCreateAuction(uint256 _tokenId) public onlyAuctionOracle {
        // Let's double check the oracle's work.
        // Note: First check returns "ERC721: invalid token ID" for non-existent token.
        require(etsToken.ownerOf(_tokenId) == etsAccessControls.getPlatformAddress(), "CTAG not owned by ETS");
        require(activeAuctions.current() < maxAuctions, "No open auction slots");
        // Get the most recent auction for this token.
        if (auctionExistsForTokenId(_tokenId)) {
            Auction memory auction = getAuctionForTokenId(_tokenId);
            require(auction.settled, "Auction exists");
        }
        _createAuction(_tokenId);
    }

    function createBid(uint256 _auctionId) public payable nonReentrant whenNotPaused {
        require(_auctionExists(_auctionId), "Auction not found");
        Auction memory auction = _getAuction(_auctionId);
        if (auction.startTime > 0) {
            require(block.timestamp < auction.endTime, "Auction ended");
        }
        require(msg.value >= reservePrice, "Must send at least reservePrice");
        require(msg.value >= auction.amount + ((auction.amount * minBidIncrementPercentage) / 100), "Bid too low");

        address payable lastBidder = auction.bidder;

        // Refund the last bidder, if applicable
        if (lastBidder != address(0)) {
            _safeTransferETHWithFallback(lastBidder, auction.amount);
        }

        if (auction.startTime == 0) {
            // Start the auction
            uint256 startTime = block.timestamp;
            uint256 endTime = startTime + duration;
            auctions[_auctionId].startTime = startTime;
            auctions[_auctionId].endTime = endTime;
        }

        auctions[_auctionId].amount = msg.value;
        auctions[_auctionId].bidder = payable(msg.sender);

        // Extend the auction if the bid was received within `timeBuffer` of the auction end time
        bool extended = false;
        if (auction.startTime > 0) {
            extended = auction.endTime - block.timestamp < timeBuffer;
            if (extended) {
                auctions[_auctionId].endTime = auction.endTime = block.timestamp + timeBuffer;
                emit AuctionExtended(_auctionId, auction.endTime);
            }
        }

        emit AuctionBid(_auctionId, msg.sender, msg.value, extended);
    }

    /**
     * @notice Function for withdrawing funds from an accrual account. Can be called by the account owner
     * or on behalf of the account. Does nothing when there is nothing due to the account.
     *
     * @param _account Address of account being drawn down and which will receive the funds.
     */
    function drawDown(address payable _account) external nonReentrant {
        uint256 balanceDue = totalDue(_account);
        if (balanceDue > 0 && balanceDue <= address(this).balance) {
            paid[_account] = paid[_account] + balanceDue;

            bool success = _safeTransferETH(_account, balanceDue);
            require(success, "Transfer failed");

            emit AuctionProceedsWithdrawn(_account, balanceDue);
        }
    }

    // ============ INTERNAL FUNCTIONS ============

    function _requestCreateAuction() private {
        // Trigger of chain oracle to pick and release next auction.
        emit RequestCreateAuction();
    }

    function _createAuction(uint256 _tokenId) internal {
        auctionId.increment();
        activeAuctions.increment();
        uint256 currentAuctionId = auctionId.current();
        // Create new auction but don't start it.
        auctions[currentAuctionId] = Auction({
            auctionId: currentAuctionId,
            tokenId: _tokenId,
            amount: 0,
            startTime: 0,
            endTime: 0,
            reservePrice: reservePrice,
            bidder: payable(address(0)),
            auctioneer: etsAccessControls.getPlatformAddress(),
            settled: false
        });

        // Add the auction Id to list of auctions for this token.
        uint256[] storage tokenAuctionIds = auctionsByTokenId[_tokenId];
        tokenAuctionIds.push(currentAuctionId);

        emit AuctionCreated(currentAuctionId, _tokenId, tokenAuctionIds.length);
        //emit AuctionCreated(_tokenId);
    }

    function _settleAuction(uint256 _auctionId) internal {
        require(_auctionExists(_auctionId), "Auction not found");
        Auction memory auction = getAuction(_auctionId);
        require(!(auction.settled), "Auction already settled");
        require(auction.startTime != 0, "Auction has not begun");
        require(block.timestamp >= auction.endTime, "Auction has not ended");

        auctions[_auctionId].settled = true;
        activeAuctions.decrement();
        etsToken.transferFrom(etsAccessControls.getPlatformAddress(), auction.bidder, auction.tokenId);
        _processAuctionRevenue(auction.tokenId, auction.amount);
        emit AuctionSettled(_auctionId);
    }

    // @dev Internal function to divide up auction revenue and accrue it to ETS participants.
    function _processAuctionRevenue(uint256 _tokenId, uint256 _amount) private {
        // Distribute proceeds to actors.
        IETSToken.Tag memory ctag = etsToken.getTagById(_tokenId);
        address platform = etsAccessControls.getPlatformAddress();

        uint256 relayerProceeds = (_amount * relayerPercentage) / MODULO;
        uint256 creatorProceeds = (_amount * creatorPercentage) / MODULO;
        uint256 platformProceeds = _amount - (relayerProceeds + creatorProceeds);

        accrued[ctag.relayer] = accrued[ctag.relayer] + relayerProceeds;
        accrued[ctag.creator] = accrued[ctag.creator] + creatorProceeds;
        accrued[platform] = accrued[platform] + platformProceeds;
    }

    /**
     * @dev Retrieves an auction given an auction ID.
     * @param _auctionId The ID of auction.
     * @return Auction object. Returns an empty object if no auction exists
     *         so must perform additional checks downstream.
     */
    function _getAuction(uint256 _auctionId) internal view returns (Auction memory) {
        return auctions[_auctionId];
    }

    function _safeTransferETHWithFallback(address to, uint256 amount) internal {
        if (!_safeTransferETH(to, amount)) {
            IWMATIC(wmatic).deposit{ value: amount }();
            IERC20Upgradeable(wmatic).transfer(to, amount);
        }
    }

    function _safeTransferETH(address to, uint256 value) internal returns (bool) {
        (bool success, ) = to.call{ value: value, gas: 30_000 }(new bytes(0));
        return success;
    }

    // Return true if tag has had an auction released in any state.
    function _auctionExists(uint256 _auctionId) internal view returns (bool) {
        return (auctions[_auctionId].auctionId != 0 && auctions[_auctionId].auctionId == _auctionId);
    }

    // ============ PUBLIC VIEW FUNCTIONS ============

    function auctionExists(uint256 _auctionId) public view returns (bool) {
        return _auctionExists(_auctionId);
    }

    function auctionExistsForTokenId(uint256 _tokenId) public view returns (bool) {
        return (auctionsByTokenId[_tokenId].length > 0);
    }

    function auctionEnded(uint256 _auctionId) public view returns (bool) {
        return (auctions[_auctionId].endTime < block.timestamp);
    }

    function auctionSettled(uint256 _auctionId) public view returns (bool) {
        return (_auctionExists(_auctionId) && auctions[_auctionId].settled);
    }

    function getAuction(uint256 _auctionId) public view returns (Auction memory) {
        return _getAuction(_auctionId);
    }

    function getAuctionForTokenId(uint256 _tokenId) public view returns (Auction memory) {
        require(auctionExistsForTokenId(_tokenId), "No auctions for tokenId");
        uint256[] memory tokenAuctionIds = auctionsByTokenId[_tokenId];
        // Return last auction for token.
        return auctions[tokenAuctionIds[tokenAuctionIds.length - 1]];
    }

    function getAuctionCountForTokenId(uint256 _tokenId) public view returns (uint256) {
        return auctionsByTokenId[_tokenId].length;
    }

    function getActiveCount() public view returns (uint256) {
        return activeAuctions.current();
    }

    function getTotalCount() public view returns (uint256) {
        return auctionId.current();
    }

    function getBalance() public view returns (uint) {
        return address(this).balance;
    }

    function totalDue(address _account) public view returns (uint256 _due) {
        return accrued[_account] - paid[_account];
    }

    /* solhint-disable */
    receive() external payable {}

    fallback() external payable {}
    /* solhint-enable */
}

contracts/ETSEnrichTarget.sol.content:
// SPDX-License-Identifier: MIT

/**
 * @title ETSEnrichTarget
 * @author Ethereum Tag Service <team@ets.xyz>
 *
 *  
 *  
 *          
 *          
 *        
 *        
 *
 * @notice Contract that handles the enrichment of Target metadata using off-chain APIs.
 *
 * In order to keep the on-chain recording of new Target records lightweight and inexpensive,
 * the createTarget() function (ETSTarget.sol) requires only a URI string (targetURI).
 *
 * To augment this, we are developing a hybrid onchain/off-chain Enrich Target flow for the purpose of
 * collecting additional metadata about a Target and saving it back on-chain.
 *
 * The flow begins with the requestEnrichTarget() function (see below) which takes a targetId as an
 * argument. If the Target exists, the function emits the targetId via the RequestEnrichTarget event.
 *
 * An OpenZeppelin Defender Sentinel is listening for this event, and when detected, passes the
 * targetId to an ETS off-chain service we call the Enrich Target API, which extracts the Target URI,
 * collects metadata about the URI and saves it in json format to IPFS. The IPFS entpoint is posted
 * back on-chain via fulfillEnrichTarget() thus updating the Target data struct.
 *
 * Future implementation should utilize ChainLink in place of OpenZeppelin for better decentralization.
 */

pragma solidity ^0.8.10;

import { IETSTarget } from "./interfaces/IETSTarget.sol";
import { IETSEnrichTarget } from "./interfaces/IETSEnrichTarget.sol";
import { IETSAccessControls } from "./interfaces/IETSAccessControls.sol";
import { ContextUpgradeable } from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract ETSEnrichTarget is IETSEnrichTarget, Initializable, ContextUpgradeable, UUPSUpgradeable {
    /// @dev ETS access controls smart contract.
    IETSAccessControls public etsAccessControls;

    /// @dev ETS access controls smart contract.
    IETSTarget public etsTarget;

    // Public constants

    string public constant NAME = "ETSEnrichTarget";

    // Modifiers

    modifier onlyAdmin() {
        require(etsAccessControls.isAdmin(_msgSender()), "Access denied");
        _;
    }

    // ============ UUPS INTERFACE ============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(IETSAccessControls _etsAccessControls, IETSTarget _etsTarget) public initializer {
        // Initialize access controls & ETS
        etsAccessControls = _etsAccessControls;
        etsTarget = _etsTarget;
    }

    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal override onlyAdmin {}

    // ============ PUBLIC INTERFACE ============

    /// @inheritdoc IETSEnrichTarget
    function requestEnrichTarget(uint256 _targetId) public {
        require(etsTarget.targetExistsById(_targetId) == true, "Invalid target");
        // require(!etsTarget.isTargetEnsured(_targetId), "Already ensured");
        emit RequestEnrichTarget(_targetId);
    }

    // ============ OWNER INTERFACE ============

    /// @inheritdoc IETSEnrichTarget
    function fulfillEnrichTarget(uint256 _targetId, string calldata _ipfsHash, uint256 _httpStatus) public {
        require(etsAccessControls.getPlatformAddress() == msg.sender, "only platform may enrich target");
        IETSTarget.Target memory target = etsTarget.getTargetById(_targetId);
        etsTarget.updateTarget(_targetId, target.targetURI, block.timestamp, _httpStatus, _ipfsHash);
    }
}

contracts/ETSRelayerFactory.sol.content:
// SPDX-License-Identifier: MIT

/**
 * @title ETS Relayer Factory
 * @author Ethereum Tag Service <team@ets.xyz>
 *
 *  
 *  
 *          
 *          
 *        
 *        
 *
 * @notice Factory contract for deploying upgradeable beacon proxy contract instances.
 * @dev see ETSRelayerBeacon.sol & ETSRelayerV1.sol
 */

pragma solidity ^0.8.10;

import { IETS } from "./interfaces/IETS.sol";
import { IETSTarget } from "./interfaces/IETSTarget.sol";
import { IETSToken } from "./interfaces/IETSToken.sol";
import { IETSAccessControls } from "./interfaces/IETSAccessControls.sol";
import { ETSRelayerBeacon } from "./relayers/ETSRelayerBeacon.sol";
import { ETSRelayerV1 } from "./relayers/ETSRelayerV1.sol";

import { Context } from "@openzeppelin/contracts/utils/Context.sol";
import { BeaconProxy } from "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";

contract ETSRelayerFactory is Context {
    mapping(uint256 => address) private vaults;

    ETSRelayerBeacon immutable etsRelayerBeacon;

    /// @dev ETS access controls contract.
    IETSAccessControls public etsAccessControls;

    /// @dev Address and interface for ETS Core.
    IETS public ets;

    /// @dev Address and interface for ETS Token
    IETSToken public etsToken;

    /// @dev Address and interface for ETS Target.
    IETSTarget public etsTarget;

    // Modifiers

    modifier onlyValidName(string calldata _name) {
        require(!etsAccessControls.isRelayerByName(_name), "Relayer name exists");
        bytes memory nameBytes = bytes(_name);
        require(nameBytes.length >= 2, "Relayer name too short");
        require(nameBytes.length <= 32, "Relayer name too long");
        _;
    }

    /// Public constants

    string public constant NAME = "ETS Relayer Factory V1";

    constructor(
        address _etsRelayerLogic,
        IETSAccessControls _etsAccessControls,
        IETS _ets,
        IETSToken _etsToken,
        IETSTarget _etsTarget
    ) {
        etsRelayerBeacon = new ETSRelayerBeacon(_etsRelayerLogic);
        etsAccessControls = _etsAccessControls;
        ets = _ets;
        etsToken = _etsToken;
        etsTarget = _etsTarget;
    }

    function addRelayer(string calldata _relayerName) external onlyValidName(_relayerName) returns (address relayer) {
        if (!etsAccessControls.isRelayerAdmin(_msgSender())) {
            require(etsToken.balanceOf(_msgSender()) > 0,"Must own CTAG");
            require(!etsAccessControls.isRelayerByOwner(_msgSender()), "Sender owns relayer");
        }

        BeaconProxy relayerProxy = new BeaconProxy(
            address(etsRelayerBeacon),
            abi.encodeWithSelector(
                ETSRelayerV1(payable(address(0))).initialize.selector,
                _relayerName,
                ets,
                etsToken,
                etsTarget,
                etsAccessControls,
                payable(_msgSender()),
                payable(_msgSender())
            )
        );

        etsAccessControls.registerRelayer(address(relayerProxy), _relayerName, _msgSender());
        return address(relayerProxy);
    }

    function getImplementation() public view returns (address) {
        return etsRelayerBeacon.implementation();
    }

    function getBeacon() public view returns (address) {
        return address(etsRelayerBeacon);
    }
}

contracts/ETSTarget.sol.content:
// SPDX-License-Identifier: MIT

/**
 * @title IETSTarget
 * @author Ethereum Tag Service <team@ets.xyz>
 *
 *  
 *  
 *          
 *          
 *        
 *        
 *
 * @notice This is core ETSTarget.sol contract for creating Target records in ETS. It includes both public
 * and administration functions.
 *
 * In ETS, a "Target" is our data structure, stored onchain, that references/points to a URI. Target records
 * are identified in ETS by their Id (targetId) which is a unsigned integer computed from the URI string.
 * Target Ids are combined with CTAG Ids by ETS core (ETS.sol) to form "Tagging Records".
 *
 * For context, from Wikipedia, URI is short for Uniform Resource Identifier and is a unique sequence of
 * characters that identifies a logical or physical resource used by web technologies. URIs may be used to
 * identify anything, including real-world objects, such as people and places, concepts, or information
 * resources such as web pages and books.
 *
 * For our purposes, as much as possible, we are restricting our interpretation of URIs to the more technical
 * parameters defined by the IETF in [RFC3986](https://www.rfc-editor.org/rfc/rfc3986). For newer protocols, such
 * as blockchains, we will lean on newer emerging URI standards such as the [Blink](https://w3c-ccg.github.io/blockchain-links)
 * and [BIP-122](https://github.com/bitcoin/bips/blob/master/bip-0122.mediawiki)
 *
 * One the thing to keep in mind with URIs & ETS Targets is that differently shaped URIs can sometimes point to the same
 * resource. The effect of that is that different Target IDs in ETS can similarly point to the same resource.
 */

pragma solidity ^0.8.10;

import { IETSTarget } from "./interfaces/IETSTarget.sol";
import { IETSEnrichTarget } from "./interfaces/IETSEnrichTarget.sol";
import { IETSAccessControls } from "./interfaces/IETSAccessControls.sol";
import { StringHelpers } from "./utils/StringHelpers.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract ETSTarget is IETSTarget, UUPSUpgradeable, StringHelpers {
    IETSAccessControls public etsAccessControls;

    IETSEnrichTarget public etsEnrichTarget;

    // Public constants

    string public constant NAME = "ETSTarget";

    /// @dev Map of targetId to Target struct.
    mapping(uint256 => Target) public targets;

    // Modifiers

    modifier onlyAdmin() {
        require(etsAccessControls.isAdmin(msg.sender), "Access denied");
        _;
    }

    // ============ UUPS INTERFACE ============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address _etsAccessControls) public initializer {
        etsAccessControls = IETSAccessControls(_etsAccessControls);
    }

    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal override onlyAdmin {}

    // ============ OWNER INTERFACE ============

    /**
     * @notice Sets ETSAccessControls on the ETSTarget contract so functions can be
     * restricted to ETS platform only. Note Caller of this function must be deployer
     * or pre-set as admin of new contract.
     *
     * @param _accessControls Address of ETSAccessControls contract.
     */
    function setAccessControls(IETSAccessControls _accessControls) public onlyAdmin {
        require(address(_accessControls) != address(0), "Address cannot be zero");
        require(_accessControls.isAdmin(msg.sender), "Caller not admin in new contract");
        etsAccessControls = _accessControls;
        emit AccessControlsSet(address(etsAccessControls));
    }

    /// @inheritdoc IETSTarget
    function setEnrichTarget(address _etsEnrichTarget) public onlyAdmin {
        require(address(_etsEnrichTarget) != address(0), "Bad address");
        etsEnrichTarget = IETSEnrichTarget(_etsEnrichTarget);
        emit EnrichTargetSet(_etsEnrichTarget);
    }

    // ============ PUBLIC INTERFACE ============

    /// @inheritdoc IETSTarget
    function getOrCreateTargetId(string memory _targetURI) public returns (uint256) {
        uint256 _targetId = computeTargetId(_targetURI);
        if (bytes(targets[_targetId].targetURI).length > 0) {
            return _targetId;
        }

        return createTarget(_targetURI);
    }

    /// @inheritdoc IETSTarget
    function createTarget(string memory _targetURI) public returns (uint256 targetId) {
        require(!targetExistsByURI(_targetURI), "target id exists");
        require(bytes(_targetURI).length > 0, "empty target");

        uint256 _targetId = computeTargetId(_targetURI);
        targets[_targetId] = Target({
            targetURI: _targetURI,
            createdBy: msg.sender,
            enriched: 0,
            httpStatus: 0,
            ipfsHash: ""
        });
        emit TargetCreated(_targetId);
        return _targetId;
    }

    /// @inheritdoc IETSTarget
    function updateTarget(
        uint256 _targetId,
        string calldata _targetURI,
        uint256 _enriched,
        uint256 _httpStatus,
        string calldata _ipfsHash
    ) external returns (bool success) {
        require(msg.sender == address(etsEnrichTarget), "Access denied");
        targets[_targetId].targetURI = _targetURI;
        targets[_targetId].enriched = _enriched;
        targets[_targetId].httpStatus = _httpStatus;
        targets[_targetId].ipfsHash = _ipfsHash;

        emit TargetUpdated(_targetId);
        return true;
    }

    // ============ PUBLIC VIEW FUNCTIONS ============

    /// @inheritdoc IETSTarget
    function computeTargetId(string memory _targetURI) public pure returns (uint256) {
        // ? Should we lowercase
        bytes32 targetId = keccak256(bytes(_targetURI));
        return uint256(targetId);
    }

    /// @inheritdoc IETSTarget
    function targetExistsByURI(string memory _targetURI) public view returns (bool) {
        uint256 targetId = computeTargetId(_targetURI);
        return targetExistsById(targetId);
    }

    /// @inheritdoc IETSTarget
    function targetExistsById(uint256 _targetId) public view returns (bool) {
        return bytes(targets[_targetId].targetURI).length > 0 ? true : false;
    }

    /// @inheritdoc IETSTarget
    function getTargetByURI(string memory _targetURI) public view returns (Target memory) {
        uint256 targetId = computeTargetId(_targetURI);
        return getTargetById(targetId);
    }

    /// @inheritdoc IETSTarget
    function getTargetById(uint256 _targetId) public view returns (Target memory) {
        return targets[_targetId];
    }
}

contracts/ETSToken.sol.content:
// SPDX-License-Identifier: MIT

/**
 * @title ETSToken
 * @author Ethereum Tag Service <team@ets.xyz>
 *
 *  
 *  
 *          
 *          
 *        
 *        
 *
 * @notice This is the core ETSToken.sol contract that governs the creation & management of
 * Ethereum Tag Service composable tags (CTAGs).
 *
 * CTAGs are ERC-721 non-fungible tokens that store a single tag string and origin attribution data
 * including a "Relayer" address and a "Creator" address. The tag string must conform to a few simple
 * validation rules.
 *
 * CTAGs are identified in ETS by their Id (tagId) which is an unsigned integer computed from the lowercased
 * tag "display" string. Given this, only one CTAG exists for a tag string regardless of its case. For
 * example, #Punks, #punks and #PUNKS all resolve to the same CTAG.
 *
 * CTAG Ids are combined with Target Ids (see ETSTarget.sol) by ETS core (ETS.sol) to form "Tagging Records".
 * See ETS.sol for more details on Tagging Records.
 */

pragma solidity ^0.8.10;

import { IETS } from "./interfaces/IETS.sol";
import { IETSToken } from "./interfaces/IETSToken.sol";
import { IETSAccessControls } from "./interfaces/IETSAccessControls.sol";
import { StringHelpers } from "./utils/StringHelpers.sol";
import { ERC721PausableUpgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721PausableUpgradeable.sol";
import { ERC721BurnableUpgradeable, ERC721Upgradeable, IERC165Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721BurnableUpgradeable.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

contract ETSToken is
    ERC721PausableUpgradeable,
    ERC721BurnableUpgradeable,
    IETSToken,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    StringHelpers
{
    IETS public ets;
    IETSAccessControls public etsAccessControls;

    // Public constants
    string public constant NAME = "CTAG Token";

    // Public variables
    uint256 public tagMinStringLength;
    uint256 public tagMaxStringLength;
    uint256 public ownershipTermLength;

    /// @dev Map of CTAG id to CTAG record.
    mapping(uint256 => Tag) public tokenIdToTag;

    /// @dev Mapping of tokenId to last renewal.
    mapping(uint256 => uint256) public tokenIdToLastRenewed;

    /// @notice Defines whether a tag has been set up as premium
    mapping(string => bool) public isTagPremium;

    /// Modifiers
    modifier onlyETSCore() {
        require(_msgSender() == address(ets), "Caller is not ETS core");
        _;
    }

    modifier onlyAdmin() {
        require(etsAccessControls.isAdmin(_msgSender()), "Access denied");
        _;
    }

    modifier onlyRelayer() {
        require(etsAccessControls.isRelayer(_msgSender()), "Caller is not relayer");
        _;
    }

    // ============ UUPS INTERFACE ============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        IETSAccessControls _etsAccessControls,
        uint256 _tagMinStringLength,
        uint256 _tagMaxStringLength,
        uint256 _ownershipTermLength
    ) public initializer {
        __ERC721_init("Ethereum Tag Service", "CTAG");
        __ERC721Pausable_init();
        __ERC721Burnable_init();
        __ReentrancyGuard_init();

        // Initialize ETSToken settings using public
        // functions so our subgraph can capture them.
        // To call them requires etsAccessControls being
        // set so we set that manually first.
        etsAccessControls = _etsAccessControls;
        setTagMinStringLength(_tagMinStringLength);
        setTagMaxStringLength(_tagMaxStringLength);
        setOwnershipTermLength(_ownershipTermLength);
    }

    // solhint-disable-next-line
    function _authorizeUpgrade(address) internal override onlyAdmin {}

    // ============ OWNER INTERFACE ============

    /**
     * @notice Sets ETS core on the ETSToken contract so functions can be
     * restricted to ETS platform only.
     *
     * @param _ets Address of ETS contract.
     */
    function setETSCore(IETS _ets) public onlyAdmin {
        require(address(_ets) != address(0), "Address cannot be zero");
        ets = _ets;
        emit ETSCoreSet(address(ets));
    }

    /**
     * @notice Sets ETSAccessControls on the ETSToken contract function calls can be
     * restricted to ETS platform only. Note: Caller of this function must be deployer
     * or pre-set as admin of new contract.
     *
     * @param _accessControls Address of ETSAccessControls contract.
     */
    function setAccessControls(IETSAccessControls _accessControls) public onlyAdmin {
        require(address(_accessControls) != address(0), "Address cannot be zero");
        require(_accessControls.isAdmin(_msgSender()), "Caller not admin in new contract");
        etsAccessControls = _accessControls;
        emit AccessControlsSet(address(etsAccessControls));
    }

    /**
     * @notice Pauses ETSToken contract.
     */
    function pause() public onlyAdmin whenNotPaused {
        _pause();
    }

    /**
     * @notice Unpauses ETSToken contract.
     */
    function unPause() public onlyAdmin whenPaused {
        _unpause();
    }

    /// @inheritdoc ERC721BurnableUpgradeable
    function burn(uint256 tokenId) public override onlyAdmin {
        _burn(tokenId);
    }

    /// @inheritdoc IETSToken
    function setTagMaxStringLength(uint256 _tagMaxStringLength) public onlyAdmin {
        tagMaxStringLength = _tagMaxStringLength;
        emit TagMaxStringLengthSet(_tagMaxStringLength);
    }

    /// @inheritdoc IETSToken
    function setTagMinStringLength(uint256 _tagMinStringLength) public onlyAdmin {
        tagMinStringLength = _tagMinStringLength;
        emit TagMinStringLengthSet(_tagMinStringLength);
    }

    /// @inheritdoc IETSToken
    function setOwnershipTermLength(uint256 _ownershipTermLength) public onlyAdmin {
        ownershipTermLength = _ownershipTermLength;
        emit OwnershipTermLengthSet(_ownershipTermLength);
    }

    /// @inheritdoc IETSToken
    function preSetPremiumTags(string[] calldata _tags, bool _enabled) public onlyAdmin {
        require(_tags.length > 0, "Empty array");
        for (uint256 i; i < _tags.length; ++i) {
            string memory tag = __lower(_tags[i]);
            isTagPremium[tag] = _enabled;
            emit PremiumTagPreSet(tag, _enabled);
        }
    }

    /// @inheritdoc IETSToken
    function setPremiumFlag(uint256[] calldata _tokenIds, bool _isPremium) public onlyAdmin {
        require(_tokenIds.length > 0, "Empty array");
        for (uint256 i; i < _tokenIds.length; ++i) {
            uint256 tokenId = _tokenIds[i];
            require(ownerOf(tokenId) == getPlatformAddress(), "Not owned by platform");
            tokenIdToTag[tokenId].premium = _isPremium;
            emit PremiumFlagSet(tokenId, _isPremium);
        }
    }

    /// @inheritdoc IETSToken
    function setReservedFlag(uint256[] calldata _tokenIds, bool _reserved) public onlyAdmin {
        require(_tokenIds.length > 0, "Empty array");
        for (uint256 i; i < _tokenIds.length; ++i) {
            uint256 tokenId = _tokenIds[i];
            require(ownerOf(tokenId) == getPlatformAddress(), "Token not owned by platform");
            tokenIdToTag[tokenId].reserved = _reserved;
            emit ReservedFlagSet(tokenId, _reserved);
        }
    }

    // ============ PUBLIC INTERFACE ============

    function getOrCreateTag(
        string calldata _tag,
        address payable _relayer,
        address payable _creator
    ) public payable returns (Tag memory tag) {
        uint256 tokenId = computeTagId(_tag);
        if (!tagExistsById(tokenId)) {
            tokenId = createTag(_tag, _relayer, _creator);
        }
        return tokenIdToTag[tokenId];
    }

    /// @inheritdoc IETSToken
    function getOrCreateTagId(
        string calldata _tag,
        address payable _relayer,
        address payable _creator
    ) public payable returns (uint256 tokenId) {
        uint256 _tokenId = computeTagId(_tag);
        if (!tagExistsById(_tokenId)) {
            _tokenId = createTag(_tag, _relayer, _creator);
        }
        return _tokenId;
    }

    /// @inheritdoc IETSToken
    function createTag(
        string calldata _tag,
        address payable _relayer,
        address payable _creator
    ) public payable nonReentrant onlyETSCore returns (uint256 _tokenId) {
        // Perform basic tag string validation.
        uint256 tagId = _assertTagIsValid(_tag);

        // mint the token, transferring it to the platform.
        _safeMint(getPlatformAddress(), tagId);

        // Store CTAG data in state.
        tokenIdToTag[tagId] = Tag({
            display: _tag,
            relayer: _relayer,
            creator: _creator,
            premium: isTagPremium[__lower(_tag)],
            reserved: isTagPremium[__lower(_tag)]
        });

        return tagId;
    }

    /// @inheritdoc IETSToken
    function renewTag(uint256 _tokenId) public {
        require(_exists(_tokenId), "ETS: CTAG not found");

        if (ownerOf(_tokenId) == getPlatformAddress()) {
            _setLastRenewed(_tokenId, 0);
        } else {
            _setLastRenewed(_tokenId, block.timestamp);
        }
    }

    /// @inheritdoc IETSToken
    function recycleTag(uint256 _tokenId) public {
        require(_exists(_tokenId), "ETS: CTAG not found");
        require(ownerOf(_tokenId) != getPlatformAddress(), "Tag owned by platform");
        require(tagOwnershipTermExpired(_tokenId), "recycling not available");

        _transfer(ownerOf(_tokenId), getPlatformAddress(), _tokenId);
        emit TagRecycled(_tokenId, _msgSender());
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC721Upgradeable, IERC165Upgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    // ============ PUBLIC VIEW FUNCTIONS ============

    /// @inheritdoc IETSToken
    function computeTagId(string memory _tag) public pure returns (uint256) {
        string memory _machineName = __lower(_tag);
        return uint256(keccak256(bytes(_machineName)));
    }

    /// @inheritdoc IETSToken
    function tagExistsByString(string calldata _tag) public view returns (bool) {
        return _exists(computeTagId(_tag));
    }

    /// @inheritdoc IETSToken
    function tagExistsById(uint256 _tokenId) public view returns (bool) {
        return _exists(_tokenId);
    }

    /// @inheritdoc IETSToken
    function tagOwnershipTermExpired(uint256 _tokenId) public view returns (bool) {
        return (getLastRenewed(_tokenId) + getOwnershipTermLength() < block.timestamp);
    }

    /// @inheritdoc IETSToken
    function getTagByString(string calldata _tag) public view returns (Tag memory) {
        return getTagById(computeTagId(_tag));
    }

    /// @inheritdoc IETSToken
    function getTagById(uint256 _tokenId) public view returns (Tag memory) {
        return tokenIdToTag[_tokenId];
    }

    /// @inheritdoc IETSToken
    function getOwnershipTermLength() public view returns (uint256) {
        return ownershipTermLength;
    }

    /// @inheritdoc IETSToken
    function getLastRenewed(uint256 _tokenId) public view returns (uint256) {
        return tokenIdToLastRenewed[_tokenId];
    }

    /// @inheritdoc IETSToken
    function getPlatformAddress() public view returns (address payable) {
        return etsAccessControls.getPlatformAddress();
    }

    /// @inheritdoc IETSToken
    function getCreatorAddress(uint256 _tokenId) public view returns (address) {
        return tokenIdToTag[_tokenId].creator;
    }

    // ============ INTERNAL FUNCTIONS ============

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 firstTokenId,
        uint256 batchSize
    ) internal override(ERC721PausableUpgradeable, ERC721Upgradeable) whenNotPaused {
        super._beforeTokenTransfer(from, to, firstTokenId, batchSize);
        require(!paused(), "Contract paused");
    }

    /// @dev See {ERC721-_afterTokenTransfer}. Contract must not be paused.
    function _afterTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal virtual override(ERC721Upgradeable) {
        super._afterTokenTransfer(from, to, tokenId, batchSize);

        if (to != address(0)) {
            // Reset token ownership term.
            if (to == getPlatformAddress()) {
                _setLastRenewed(tokenId, 0);
            } else {
                _setLastRenewed(tokenId, block.timestamp);
            }
        }

        // If from address is not black hole or platform, and balance is going zero pause their relayer.
        if (from != address(0) && from != getPlatformAddress() && balanceOf(from) == 0) {
            etsAccessControls.pauseRelayerByOwnerAddress(from);
        }
    }

    /**
     * @dev Private method used for validating a CTAG string before minting.
     *
     * A series of assertions are performed reverting the transaction for any validation violations.
     *
     * @param _tag Proposed tag string.
     */
    function _assertTagIsValid(string memory _tag) private view returns (uint256 _tagId) {
        // generate token ID from machine name
        uint256 tagId = computeTagId(_tag);

        require(!_exists(tagId), "ERC721: token already minted");

        bytes memory tagStringBytes = bytes(_tag);
        require(
            tagStringBytes.length >= tagMinStringLength && tagStringBytes.length <= tagMaxStringLength,
            "Invalid tag format"
        );

        require(tagStringBytes[0] == 0x23, "Tag must start with #");

        // start from first char after #
        for (uint256 i = 1; i < tagStringBytes.length; i++) {
            bytes1 char = tagStringBytes[i];
            require(char != 0x20, "Spaces in tag");
            require(char != 0x23, "Tag contains prefix");
        }

        return tagId;
    }

    function _setLastRenewed(uint256 _tokenId, uint256 _timestamp) internal {
        tokenIdToLastRenewed[_tokenId] = _timestamp;
        emit TagRenewed(_tokenId, msg.sender);
    }
}

